import React, {useEffect, useState} from 'react';
import { useGitHub } from '../../hooks/github_hooks';
import { RepositoryItem } from '../Repository_item';
import * as S from './styled';


export const Repositories = () => {
  const { gitHubState, getUserRepos, getUserStarred } = useGitHub();
  const [hasUserForSearchRepos, setHasUserForSearchRepos] = useState(false);

  useEffect(() => {
    if (gitHubState.user.login) {
      getUserRepos(gitHubState.user.login);
      getUserStarred(gitHubState.user.login);
    }
    setHasUserForSearchRepos(gitHubState.repositories);

  }, [gitHubState.user.login]);



  return ( 

    <>
      {hasUserForSearchRepos ? (
        <S.WrapperTabs
        // react-tab-npm faz ser exibida qual tab foi clicada Repositories || Starred; 
          selectedTabClassName="is-selected"
          selectedTabPanelClassName="is-selected"
        >
          <S.WrapperTabList>
            <S.WrapperTab>Repositories</S.WrapperTab>
            <S.WrapperTab>Starred</S.WrapperTab>
          </S.WrapperTabList>
          <S.WrapperTabPanel>
            <RepositoryItem
              name="GitHub_Api_Interface_By_DIO"
              linkToRepo="https://github.com/mbrennerr/GitHub_Api_Interface_By_DIO"
              fullName="mbrennerr/GitHub_Api_Interface_By_DIO"
            />
          </S.WrapperTabPanel>
          <S.WrapperTabPanel>
            <RepositoryItem
            name="mbtbwarts"
            linkToRepo="https://github.com/mbrennerr/mbtbwarts"
            fullName="mbrennerr/mbtbwarts"
            />
          </S.WrapperTabPanel>
        </S.WrapperTabs>
      ) : 
      (
        <></>
      )}  
    </>  
  );
};
