import styled from 'styled-components';

export const Wrapper = styled.div`
  display:grid;
  width: 600%;
  height: 200px;
  border: 1px solid #202020;


  input {
    border: 1px solid #ccc;
    border-radius: 8px;
    width: 90%;
    height: 50px;
    padding: 10px;
    font-weight: 500;   

  }
  button{
    background-color: #225ed8;
    padding: 0 20px; 
    border-radius: 8px;
    font-weight: bold;
    font-size:16px;

    span {
      font-weight: bold;
      color: #ffff;
    }

    &:hover{
      background-color: #2c5282;
      box-shadow: 3px 2px 10px rgba(0, 0, 0, 0.2);
    }

  }
`;
