import React, { useState, createContext } from 'react';

export const GithubContext = createContext({
  loading: false,
  user: {},
  repositories: [],
  starred: [],

});






export const GithubProvider = ({ children }) => {
  const [githubState, setgithubState] = useState({
    loading: false,
    user: {
      login: undefined,
      name: undefined,
      html_url: undefined,
      blogUrl: undefined,
      company: undefined,
      followers: 15000,
      following: 50,
      public_gists: 60,
      public_repos: 70,

    },
    repositories: [],
    starred: [3],
  })

  const contextValue = {
    githubState,
  }

  return (
    <GithubContext.Provider value={contextValue}>
      {children}
    </GithubContext.Provider>
  )
};
